	public static String getNonce(String address) {
		Result<Map<String, String>> addressByPriKey = NulsSDKTool.getAccountBalance(address, 1, 1);

		return addressByPriKey.getData().get("nonce");
	}

	public static String getNonce(String walletUrl, String address) {

		try {
			Map<String, Object> params = new HashMap<>();
			params.put("assetChainId", 1);
			params.put("assetId", 1);
			String resultStr = HttpRequest.post(walletUrl + "api/accountledger/balance/" + address).form(params).execute().body();
			RestFulResult<Map<String, Object>> result = toResult(resultStr);
			if (result.isSuccess()) {
				return result.getData().get("nonce").toString();
			}
			return null;
		} catch (Exception e) {
			Log.error(e);
			return null;
		}
	}

	private static RestFulResult toResult(String str) throws IOException {
		Map<String, Object> resultMap = JSONUtils.json2map(str);
		RestFulResult result = null;
		Boolean b = (Boolean) resultMap.get("success");
		if (b) {
			result = RestFulResult.success(resultMap.get("data"));
		} else {
			Object dataObj = resultMap.get("data");
			if (dataObj instanceof Map) {
				Map<String, Object> data = (Map<String, Object>) resultMap.get("data");
				if (data != null) {
					result = RestFulResult.failed(data.get("code").toString(), data.get("msg").toString());
				}
			} else {
				result = RestFulResult.failed(CommonCodeConstanst.SYS_UNKOWN_EXCEPTION.getCode(), resultMap.toString());
			}
		}
		return result;
	}

