	public static boolean getTx(String txId) {
		Result tx = NulsSDKTool.getTx(txId);

		if (tx.isFailed()) {
			System.out.println("getTx errr:" + JSON.toJSONString(tx));
			return false;
		} else {
			System.out.println("getTx:" + JSON.toJSONString(tx.getData()));
		}
		return true;
	}

	public static Map<String, Object> getAccountBalance(String address) {
		Map<String, Object> chainInfo = getInfo();
		Map<String, Object> accountInfo = (Map) NulsSDKTool.getAccountBalance(address, Integer.valueOf(chainInfo.get("chainId").toString()),
				Integer.valueOf(chainInfo.get("assetId").toString())).getData();
		System.out.println("accountInfo:" + JSON.toJSONString(accountInfo));
		return accountInfo;
	}
